import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utlis';

const Login = () => {
  // ======================= STATE =======================
  const [loginInfo, setLoginInfo] = useState({
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  // ======================= HANDLE INPUT CHANGE =======================
  const handleChange = (e) => {
    const { name, value } = e.target; 
    setLoginInfo((prev) => ({ ...prev, [name]: value })); // update state dynamically
  };

  // ======================= HANDLE LOGIN =======================
  const handleLogin = async (e) => {
  e.preventDefault();

  try {
    const response = await fetch("http://localhost:5000/auth/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });

    const result = await response.json();
    console.log("API Response:", result);

    // Check for message instead of success/status
    if (result.message === "Login successful") {
      localStorage.setItem("token", result.token);
      localStorage.setItem("loggedInUser", result.user.name);

      handleSuccess(result.message);
      console.log("Login successful, navigating to /home...");

      setTimeout(() => {
        navigate("/home");
      }, 1000);
    } else {
      handleError(result.message || "Invalid login attempt");
    }
  } catch (err) {
    console.error("Login error:", err);
    handleError("Something went wrong. Please try again later.");
  }
};


  // ======================= JSX =======================
  return (
    <div className='Container'>
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor='email'>Email</label>
          <input
            onChange={handleChange}
            type='email'
            name='email'
            placeholder='Enter your email...'
            value={loginInfo.email}
          />
        </div>
        <div>
          <label htmlFor='password'>Password</label>
          <input
            onChange={handleChange}
            type='password'
            name='password'
            placeholder='Enter your Password...'
            value={loginInfo.password}
          />
        </div>
        <button type='submit'>Login</button>
        <span>
          Donâ€™t have an account? <Link to='/signup'>Signup</Link>
        </span>
      </form>
      <ToastContainer/>
    </div>
  );
};

export default Login;
