import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { ToastContainer } from "react-toastify";
import { handleError, handleSuccess } from "../utlis"; // your toast utils

const Login = () => {
  // ✅ define state for email and password
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // react-router navigation
  const navigate = useNavigate();

  // handle login
  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }), // using state variables
      });

      const result = await response.json();
      console.log("API Response:", result);

      if (result.message === "Login successful") {
        // save token and user info
        localStorage.setItem("token", result.token);
        localStorage.setItem("loggedInUser", result.user.name);

        handleSuccess(result.message);
        console.log("Login successful, navigating to /home...");

        setTimeout(() => {
          navigate("/home");
        }, 1000);
      } else {
        handleError(result.message || "Invalid login attempt");
      }
    } catch (err) {
      console.error("Login error:", err);
      handleError("Something went wrong. Please try again later.");
    }
  };

  return (
    <div>
      <h2>Login</h2>

      <form onSubmit={handleLogin}>
        {/* Email */}
        <div>
          <label>Email</label>
          <input
            type="email"
            placeholder="test@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // ✅ update state
            required
          />
        </div>

        {/* Password */}
        <div>
          <label>Password</label>
          <input
            type="password"
            placeholder="********"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // ✅ update state
            required
          />
        </div>

        <button type="submit">Login</button>
      </form>

      <p>
        Don’t have an account? <Link to="/signup">Signup</Link>
      </p>

      <ToastContainer />
    </div>
  );
};

export default Login;
