import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utlis';

const Login = () => {
  // ======================= STATE =======================
  const [loginInfo, setLoginInfo] = useState({
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  // ======================= HANDLE INPUT CHANGE =======================
  const handleChange = (e) => {
    const { name, value } = e.target; 
    setLoginInfo((prev) => ({ ...prev, [name]: value })); // update state dynamically
  };

  // ======================= HANDLE LOGIN =======================
  const handleLogin = async (e) => {
    e.preventDefault(); // prevent default page refresh

    const { email, password } = loginInfo;

    // 1. Validate required fields
    if (!email || !password) {
      return handleError('Email and password are required');
    }

    try {
      // 2. API request to backend
      const url = 'http://localhost:5000/auth/login';
      const response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginInfo),
      });

      // 3. Parse JSON response
      const result = await response.json();
      console.log('Login Response:', result);

      const { success, message, jwtToken, name } = result;

      // 4. If login success
      if (success) {
        // ✅ show success toast in green
        handleSuccess(message || 'Login successful!');

        // ✅ Save token + username to localStorage
        if (jwtToken) localStorage.setItem('token', jwtToken);
        if (name) localStorage.setItem('loggedInuser', name);

        // ✅ Redirect to home page after short delay (for toast to display)
        setTimeout(() => {
          navigate('/home');
        }, 1200);

      } else {
        // 5. If login fails
        handleError(message || 'Login failed');
      }

    } catch (err) {
      console.error('Login error:', err);
      handleError(err.message || 'Something went wrong. Please try again.');
    }
  };

  // ======================= JSX =======================
  return (
    <div className='Container'>
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor='email'>Email</label>
          <input
            onChange={handleChange}
            type='email'
            name='email'
            placeholder='Enter your email...'
            value={loginInfo.email}
          />
        </div>
        <div>
          <label htmlFor='password'>Password</label>
          <input
            onChange={handleChange}
            type='password'
            name='password'
            placeholder='Enter your Password...'
            value={loginInfo.password}
          />
        </div>
        <button type='submit'>Login</button>
        <span>
          Don’t have an account? <Link to='/signup'>Signup</Link>
        </span>
      </form>

      {/* ✅ ToastContainer with green success */}
      <ToastContainer
        position='top-right'
        autoClose={1500}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme='green' // ✅ makes success green and error red
      />
    </div>
  );
};

export default Login;
