import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import{ToastContainer} from 'react-toastify'
import { handleError, handleSuccess } from '../utlis'

const Signup = () => {

const [signupInfo, setsignUpInfo] = useState({
    name: '',
    email:'',
    password: '',
})
const navigate = useNavigate ();

   const handleChange= (e) =>{
   const {name, value}= e.target;
   console.log(name, value);
   const copySignupInfo= {...signupInfo};
   copySignupInfo [name] = value;
   setsignUpInfo (copySignupInfo);
   }

console.log ('signupInfo ->', signupInfo) // prevent page from Refresh

const handleSignup = async (e) => {
  e.preventDefault()
  const { name, email, password } = signupInfo;
  if (!name || !email || !password) {
    return handleError('Name, email, and password are required')
  }
  try {
    const url = "http://localhost:5000/auth/signup";
    const response = await fetch(url, {
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(signupInfo)
    });
    
    // Check if response is ok first
    if (!response.ok) {
      // Try to get error message from response
      let errorMessage = `Server error: ${response.status}`;
      try {
        const errorData = await response.json();
        errorMessage = errorData.message || errorMessage;
      } catch (parseError) {
        // If response is not JSON, get text instead
        const text = await response.text();
        errorMessage = text || errorMessage;
      }
      throw new Error(errorMessage);
    }
    
    // If response is ok, parse as JSON
    const result = await response.json();
    const {success, message} = result;
    if (success){
        handleSuccess (message);
       setTimeout(() => {
        navigate ('/login')
       }, 1000);
    }
    console.log('Signup successful:', result);
    
    // Handle successful signup here (redirect, show success message, etc.)
    
  } catch (err) {
    console.error('Signup error:', err);
    handleError(err.message || 'Signup failed. Please try again.');
  }
}


  return (
    <div className='Container'>
    <h1>Signup</h1>
    <form onSubmit={handleSignup}>
        <div>
            <label htmlFor='name'>Name</label>
            <input 
            onChange={handleChange}
            type='text'
            name='name'
            autoFocus
            placeholder='Enter your name...'
            value={signupInfo.name}
            />
        </div>
        <div>
            <label htmlFor='email'>Email</label>
            <input 
           onChange={handleChange}
            type='email'
            name='email'
            placeholder='Enter your email...'
         value={signupInfo.email}

            />
        </div>
        <div>
            <label htmlFor='password'>Password</label>
            <input 
             onChange={handleChange}
            type='password'
            name='password'
            placeholder='Enter your Password...'
             value={signupInfo.password}

            />
        </div>
        <button type='submit'>Signup</button>
        <span>
            Already have an account?
            <Link to="/login">Login</Link>
        </span>
    </form>
    <ToastContainer/>
    </div>
    
  )
}

export default Signup
