import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import{ToastContainer} from 'react-toastify'
import { handleError, handleSuccess } from '../utlis'

const Signup = () => {

const [signupInfo, setsignUpInfo] = useState({
    name: '',
    email:'',
    password: '',
})
const navigate = useNavigate ();

   const handleChange= (e) =>{
   const {name, value}= e.target;
   console.log(name, value);
   const copySignupInfo= {...signupInfo};
   copySignupInfo [name] = value;
   setsignUpInfo (copySignupInfo);
   }

console.log ('signupInfo ->', signupInfo) // prevent page from Refresh

const handleSignup = async (e) => {
  e.preventDefault()
  const { name, email, password } = signupInfo;
  if (!name || !email || !password) {
    return handleError('Name, email, and password are required')
  }
  try {
    const url = "http://localhost:5000/auth/signup";
    console.log('Sending request to:', url);
    console.log('Request body:', signupInfo);
    
    const response = await fetch(url, {
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(signupInfo)
    });
    
    console.log('Response status:', response.status);
    console.log('Response ok:', response.ok);
    
    // Get the response text first to see what we're actually getting
    const responseText = await response.text();
    console.log('Raw response:', responseText);
    
    let result;
    try {
      result = JSON.parse(responseText);
      console.log('Parsed result:', result);
    } catch (parseError) {
      console.error('Failed to parse JSON:', parseError);
      throw new Error(`Server returned invalid JSON: ${responseText}`);
    }
    
    if (!response.ok) {
      throw new Error(result.message || `Server error: ${response.status}`);
    }
    
    // Check if success property exists in the response
    if (result.success) {
      handleSuccess(result.message || 'Signup successful!');
      setTimeout(() => {
        navigate('/login');
      }, 1000);
    } else {
      // Handle case where success is false but status is 200
      handleError(result.message || 'Signup failed for unknown reason');
    }
    
  } catch (err) {
    console.error('Signup error details:', err);
    handleError(err.message || 'Signup failed. Please try again.');
  }
}


  return (
    <div className='Container'>
    <h1>Signup</h1>
    <form onSubmit={handleSignup}>
        <div>
            <label htmlFor='name'>Name</label>
            <input 
            onChange={handleChange}
            type='text'
            name='name'
            autoFocus
            placeholder='Enter your name...'
            value={signupInfo.name}
            />
        </div>
        <div>
            <label htmlFor='email'>Email</label>
            <input 
           onChange={handleChange}
            type='email'
            name='email'
            placeholder='Enter your email...'
         value={signupInfo.email}

            />
        </div>
        <div>
            <label htmlFor='password'>Password</label>
            <input 
             onChange={handleChange}
            type='password'
            name='password'
            placeholder='Enter your Password...'
             value={signupInfo.password}

            />
        </div>
        <button type='submit'>Signup</button>
        <span>
            Already have an account?
            <Link to="/login">Login</Link>
        </span>
    </form>
    <ToastContainer/>
    </div>
    
  )
}

export default Signup
